import axios from "axios";
const Post = ({ id, comment }) => {
  return (
    <div>
      <h1>post# {id}</h1>
    </div>
  );
};

Post.getInitialProps = async ({ query }) => {
  const res = await axios.get(
    "https://jsonplaceholder.typicode.com/comments?postId=3"
  );
  const { data } = res;
  console.log(data[0]);
  return { ...query, comments: data };
};

export default Post;
